  const [right, setRight] = useState(0);
  const [ans, setAns] = useState(null);
  return (
    <div className="calc">
      <header>電卓</header>
      <div className="display">
        {left}
        {ope === null ? "" : ope}
        {ope === null ? "" : right}
      </div>
      <div className="input">
        <div className="numbers">
          {/* ここに数字のボタンや「.」、「=」ボタン */}
        </div>
        <div className="operators">
          {/* ここに「+」、「-」、「*」、「/」ボタン */}
        </div>
      </div>
    </div>
  );
}

export default App;
