  }


  return (
    <div className="calc">
      <header>電卓</header>
      <div className="display">
        {left}
        {ope === null ? "" : ope}
        {ope === null ? "" : right}
      </div>
      <div className="input">
        <div className="numbers">
          <Button text="0" onClick={() => { keyPressed(0) }} />
